const Header = (course) =>{
  console.log(course)
  return(
    <div>
      <h1>{course.name}</h1>
    </div>
  )
}

const Part = (element) =>{
  return(
    <div>
      <p>
        {element.part} {element.exercises}
      </p>
    </div>
  )
}

const Content = (parts) =>{
  return(
    <div>
      <Part part={parts.parts[0].name} exercises={parts.parts[0].exercises}/>
      <Part part={parts.parts[1].name} exercises={parts.parts[1].exercises}/>
      <Part part={parts.parts[2].name} exercises={parts.parts[2].exercises}/>
    </div>
  )
}

const Total = (number) =>{
  return(
    <div>
       <p>Number of exercises {number.parts[0].exercises + number.parts[1].exercises + number.parts[2].exercises}</p>
    </div>
  )
}

const App = () => {

  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]


  return (
    <div>
      <Header name={course} />
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

export default App
