import { useState } from 'react'

const Display = props => <div>{props.name}{props.value}{props.operator}</div>

const Button = ({ onClick, text }) => <button onClick={onClick}>{text}</button>

const statistics = (props) =>  {
  <div>
    <h1>statistics</h1>
    <Display name="good " value={props.good} />
    <Display name="neutral " value={props.neutral} />
    <Display name="bad " value={props.bad} />
    <Display name="all  " value={props.total} />
    <Display name="average  " value={props.average} />
    <Display name="positive  " value={props.positive} operator="%"/>
  </div>
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const increaseGood = () => setGood(good + 1)
  const increaseNeutral = () => setNeutral(neutral + 1)
  const increaseBad = () => setBad(bad + 1)

  let total = good + neutral + bad
  let average = (good -bad)/total
  let positive = (good/total)*100
  return (
    <div>
      <h1>give feedback</h1>
      <br></br>
      <Button
        onClick={increaseGood}
        text='good'
      />
      <Button
        onClick={increaseNeutral}
        text='neutral'
      />
      <Button
        onClick={increaseBad}
        text='bad'
      />
      <br></br>
      statistics
    </div>
  )
}

export default App
