const Header = (course) =>{
  return(
    <div>
      <h1>{course.name}</h1>
    </div>
  )
}

const Part = (element) =>{
  return(
    <div>
      <p>
        {element.part} {element.exercises}
      </p>
    </div>
  )
}

const Content = (parts) =>{
  return(
    <div>
      <Part part={parts.part1} exercises={parts.exercises1}/>
      <Part part={parts.part2} exercises={parts.exercises2}/>
      <Part part={parts.part3} exercises={parts.exercises3}/>
    </div>
  )
}

const Total = (number) =>{
  return(
    <div>
       <p>Number of exercises {number.exercises1 + number.exercises2 + number.exercises3}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header name={course} />
      <Content part1={part1} exercises1={exercises1} />
      <Total exercises1={exercises1} exercises2={exercises2} exercises3={exercises3} />
    </div>
  )
}

export default App
