const Part = ({partname}) => {
  return (
    <p>{partname.name}</p>
  )
}

const Content = ({content}) => {
  return (
    <div>
    {content.map(part =>
      <Part key={part.id} partname={part.name} partex={part.exercises}/>)}
    </div>
  )
}


const Course = ({course}) =>{
  return(
      <div>
        <Header courseName={course}></Header>
        <Content content={course.parts} />
      </div>
  )
}

const App = () => {
  const course = {
    id: 1,
