const Header = ({course}) =>{
  return(
      <h1>{course.name}</h1>
  )
}

const Part = ({element}) =>{
  return(
    <div>
      <p>
        {element.part.name} {element.part.exercises}
      </p>
    </div>
  )
}

const Content = ({parts}) =>{
  return(
    <div>
      {parts.parts.map((part) => <Part element={part} />)}
    </div>
  )
}

