import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [searchName, setSearchName] = useState('')

  const addPerson = (event) =>{
    event.preventDefault()
    var nameCheck = persons.find(person => person.name === newName)
    if(typeof nameCheck === 'undefined'){
      const nameObject ={
        name: newName,
        number: newNumber,
        id: persons.length + 1,
      }
      setPersons(persons.concat(nameObject))
      setNewName('')
      setNewNumber('')
    }
    else{
      window.alert(`${newName} is already added to phonebook`);
    }
  }

  const handlePersonChange = (event) =>{
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) =>{
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const handleSearchname = (event) =>{
    console.log(event.target.value)
    setSearchName(event.target.value)
  }

  const personToShow = persons.filter(person => person.name.toLowerCase().includes(searchName))

  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>Filter shown with:<input value={searchName} onChange={handleSearchname}/></div>
      </form>
      <h2>Add a new</h2>
      <form onSubmit={addPerson}>
        <div>Name: <input value={newName} onChange={handlePersonChange}/></div>
        <div>Number: <input value={newNumber} onChange={handleNumberChange}/></div>
        <div>
          <button type="submit">Add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {personToShow.map(person => 
          <li key={person.id}> {person.name} {person.number}</li>
          )}
      </ul>
    </div>
  )
}

export default App
